public class GetQueueNameResponsePacket extends Packet {
    public String name;

    public GetQueueNameResponsePacket() {
    }

    public GetQueueNameResponsePacket(String name) {
        this.name = name;
    }
    
    public GetQueueNameResponsePacket(int packetId, String name) {
        this.packetId = packetId;
        this.name = name;
    }

    @Override
    public byte methodId() {
        return 40;
    }

    @Override
    public void emit(ByteBuffer buffer) {
        int lengthIndex = buffer.position();
        buffer.position(lengthIndex + 2);
        buffer.put(methodId());
        buffer.putInt(packetId);
        putString(buffer, name);
        buffer.putShort(lengthIndex, (short) (buffer.position() - lengthIndex - 2));
    }

    @Override
    public void parse(ByteBuffer buffer) {
        packetId = buffer.getInt();
        name = getString(buffer);
    }

    @Override
    public GetQueueNameResponsePacket newInstance() {
        return new GetQueueNameResponsePacket();
    }
    
    public Packet getHandled(ProtocolHandler handler) throws PacketProcessingException {
        return handler.handle((GetQueueNameResponsePacket) this);
    }

    public String toString() {
        return "GetQueueNameResponsePacket";
    }
}